import { useState } from "react";

import { Container, Grid, Image, Item, Button } from "semantic-ui-react";
import "./App.css";


function App() {
  const [origImage, setOrigImage] = useState("");
  const [origImageFile, setOrigImageFile] = useState("");
  const [compressedImage, setCompressedImage] = useState("");
  const [fileName, setFileName] = useState("");

  const handle = (e) => {

    const imageFile = e.target.files[0];
    setOrigImage(imageFile);
    setOrigImageFile(URL.createObjectURL(imageFile));
    setFileName(imageFile.name);
  };
  

   
   
  return (
    <div className="App">
      <h1></h1>
      <Container>
        <Grid>
          <Grid.Column width={6}>
            <Item>
              {origImageFile ? (
                <Image src={origImageFile}></Image>

              ) : (

                <Image src="http://navparivartan.in/wp-content/uploads/2018/11/placeholder.png"></Image>
              )}
            </Item>
          </Grid.Column>
          <Grid.Column width={4}>
            <input
              type="file"
              accept="image/*"
              className="mt-2 btn btn-dark w-75"
              onChange={(e) => handle(e)}
            />
              <Button>
                <a href={compressedImage} download={fileName}>
                  {" "}
                  Download Image
                </a>
              </Button>

          </Grid.Column>
        </Grid>
      </Container>
    </div>
  );

}



export default App;
