import { createContext } from 'react'

import { ScrollerMotionValues } from './types'

export const Context = createContext<ScrollerMotionValues | undefined>(
  undefined
)
import { useMemo } from 'react'
import { useTransform } from 'framer-motion'

import { MotionValue } from '../../types'

interface Options {
  axisSpring: MotionValue
  scale: number
  refSize: number
  windowSize: number
}

export const useAxis = ({
  axisSpring,
  scale,
  refSize,
  windowSize
}: Options) => {
  const scaledSize = useMemo(
    () => (refSize > windowSize ? refSize * scale : refSize),
    [refSize, scale, windowSize]
  )

  const transformFrom = useMemo(
    () => [0, scaledSize - windowSize],
    [scaledSize, windowSize]
  )
  const transformTo = useMemo(
    () => [0, (refSize - windowSize) * -1],
    [refSize, windowSize]
  )

  const axis = useTransform(axisSpring, transformFrom, transformTo, {
    clamp: false
  })

  return {
    axis,
    size: scaledSize
  }
}
import { useEffect, useState } from 'react'
import debounceFn from 'debounce-fn'

import { ChildrenRef } from '../../types'

const getSize = (el: HTMLElement | null) => ({
  height: el?.scrollHeight ?? 0,
  width: el?.scrollWidth ?? 0
})

const supportsResizeObserver = typeof ResizeObserver === 'function'

export const useSize = (ref: ChildrenRef) => {
  const [size, setSize] = useState({ height: 0, width: 0 })

  useEffect(() => {
    const onResize = () => setSize(getSize(ref.current))
    const debouncedOnResize = debounceFn(onResize, { wait: 400 })
    let resizeObserver: ResizeObserver | null = null

    if (ref.current) {
      onResize()
      window.addEventListener('resize', debouncedOnResize)

      if (supportsResizeObserver) {
        resizeObserver = new ResizeObserver(onResize)
        resizeObserver.observe(ref.current)
      }
    }

    return () => {
      window.removeEventListener('resize', debouncedOnResize)

      if (resizeObserver) {
        resizeObserver.disconnect()
      }
    }
  }, [])

  return size
}
import { useContext } from 'react'

import { Context } from '../context'

export const useScrollerMotion = () => {
  const context = useContext(Context)

  if (context === undefined) {
    throw new Error('useScrollerMotion must be used within ScrollerMotion')
  }

  return context
}
import { FC, useMemo } from 'react'
import { motion } from 'framer-motion'

import { ChildrenRef, DivElementProps, MotionValue } from '../../types'

interface Props extends DivElementProps {
  childrenRef: ChildrenRef
  disabled?: boolean
  height: number
  width: number
  x: MotionValue
  y: MotionValue
}

const FIXED_STYLE = {
  position: 'fixed',
  left: 0,
  top: 0,
  right: 0,
  bottom: 0
}

export const Wrap: FC<Props> = ({
  children,
  childrenRef,
  disabled,
  height,
  style,
  width,
  x,
  y,
  ...props
}) => {
  const outerStyle = useMemo(
    () => ({ ...style, ...(!disabled ? { height, width } : {}) }),
    [disabled, height, style, width]
  )
  const fixedStyle = useMemo(() => (!disabled ? FIXED_STYLE : {}), [disabled])
  const motionStyle = useMemo(
    () => ({ x: !disabled ? x : 0, y: !disabled ? y : 0 }),
    [disabled, x, y]
  )

  return (
    <div {...props} style={outerStyle}>
      <div style={fixedStyle}>
        <motion.div style={motionStyle} ref={childrenRef}>
          {children}
        </motion.div>
      </div>
    </div>
  )
}
import { forwardRef, useImperativeHandle, useMemo, useRef } from 'react'

import { Context } from '../../context'
import { useCore } from '../../hooks'
import { CoreProps, CoreRef } from '../../types'

import { Wrap } from './Wrap'

const DEFAULT_SCALE = 1

const DEFAULT_SPRING = {
  damping: 50,
  mass: 1.25,
  stiffness: 200
}

export const Core = forwardRef<CoreRef, CoreProps>(
  (
    {
      children,
      disabled,
      scale = DEFAULT_SCALE,
      spring = DEFAULT_SPRING,
      ...props
    },
    ref
  ) => {
    const childrenRef = useRef(null)

    const { height, width, scrollX, scrollY, x, y } = useCore({
      ref: childrenRef,
      scale,
      spring
    })

    useImperativeHandle(ref, () => ({ scrollX, scrollY, x, y }), [
      scrollX,
      scrollY,
      x,
      y
    ])

    const contextValue = useMemo(
      () => ({ scrollX, scrollY, x, y }),
      [scrollX, scrollY, x, y]
    )

    return (
      <Wrap
        {...props}
        childrenRef={childrenRef}
        disabled={disabled}
        height={height}
        width={width}
        x={x}
        y={y}
      >
        <Context.Provider value={contextValue}>{children}</Context.Provider>
      </Wrap>
    )
  }
)

Core.displayName = 'ScrollerMotion'
import { useMemo } from 'react'

import { ChildrenRef, SpringProp } from '../../types'

import { useAxis } from './useAxis'
import { useSize } from './useSize'
import { useSpringScroll } from './useSpringScroll'
import { useWindowSize } from './useWindowSize'

interface Options {
  ref: ChildrenRef
  scale: number
  spring: SpringProp
}

export const useCore = ({ ref, scale, spring }: Options) => {
  const innerScale = useMemo(() => Math.max(1, scale), [scale])

  const { height: windowHeight, width: windowWidth } = useWindowSize()
  const { height: refHeight, width: refWidth } = useSize(ref)

  const { x: springX, y: springY } = useSpringScroll(spring)

  const { axis: x, size: width } = useAxis({
    axisSpring: springX,
    scale: innerScale,
    refSize: refWidth,
    windowSize: windowWidth
  })

  const { axis: y, size: height } = useAxis({
    axisSpring: springY,
    scale: innerScale,
    refSize: refHeight,
    windowSize: windowHeight
  })

  return { height, width, scrollX: springX, scrollY: springY, x, y }
}
import { useEffect, useState } from 'react'

const getWindowSize = () => ({
  width:
    window.innerWidth ||
    document.documentElement.clientWidth ||
    document.body.clientWidth,
  height:
    window.innerHeight ||
    document.documentElement.clientHeight ||
    document.body.clientHeight
})

export const useWindowSize = () => {
  const [size, setSize] = useState({ height: 0, width: 0 })

  useEffect(() => {
    const onResize = () => setSize(getWindowSize())

    onResize()
    window.addEventListener('resize', onResize, false)

    return () => window.removeEventListener('resize', onResize, false)
  }, [])

  return size
}
import { useSpring, useViewportScroll } from 'framer-motion'

import { MotionValue, SpringProp, SpringOptions } from '../../types'

export const useSpringScroll = (springConfig: SpringProp) => {
  const { scrollX, scrollY } = useViewportScroll()

  const config = typeof springConfig === 'object' ? springConfig : undefined
  const springX: MotionValue<number> = useSpring(
    scrollX,
    config as SpringOptions
  )
  const springY: MotionValue<number> = useSpring(
    scrollY,
    config as SpringOptions
  )

  return {
    x: config ? springX : scrollX,
    y: config ? springY : scrollY
  }
}
